INSERT INTO sentence (title, text, url) VALUES ('Spring (device)', 'When a conventional spring, without stiffness variability features, is compressed or stretched from its resting position, it exerts an opposing force approximately proportional to its change in length (this approximation breaks down for larger deflections).', 'https://en.wikipedia.org/wiki/Spring_(device)');
INSERT INTO sentence (title, text, url) VALUES ('Hydnum repandum', 'Hydnum repandum, commonly known as the sweet tooth, wood hedgehog or hedgehog mushroom, is a basidiomycete fungus of the family Hydnaceae.', 'https://en.wikipedia.org/wiki/Hydnum_repandum');
INSERT INTO sentence (title, text, url) VALUES ('IndyCar Classic', 'Circuit of the Americas (COTA) in Austin, Texas, was built to Formula One (F1) specifications to host the United States Grand Prix starting in 2012.', 'https://en.wikipedia.org/wiki/IndyCar_Classic');
INSERT INTO sentence (title, text, url) VALUES ('Jude Akuwudike', 'Jude Akuwudike (born 1965) is a Nigerian-born actor educated in England. He has mostly worked there, on stage and screen.', 'https://en.wikipedia.org/wiki/Jude_Akuwudike');
INSERT INTO sentence (title, text, url) VALUES ('Fiery-browed starling', 'Distinguished by a reddish-orange stripe over the eye, it is endemic to the Indonesian island of Sulawesi, mainly living in humid highland forest.', 'https://en.wikipedia.org/wiki/Fiery-browed_starling');
INSERT INTO sentence (title, text, url) VALUES ('Yukio Mishima', 'Yukio Mishima (三島 由紀夫 Mishima Yukio) is the pen name of Kimitake Hiraoka (平岡 公威 Hiraoka Kimitake, January 14, 1925 – November 25, 1970), a Japanese author, poet, playwright, actor, model, film director, nationalist, and founder of the Tatenokai. Mishima is considered one of the most important Japanese authors of the 20th century.', 'https://en.wikipedia.org/wiki/Yukio_Mishima');
INSERT INTO sentence (title, text, url) VALUES ('HMS Highlander (H44)', 'HMS Highlander was an H-class destroyer that had originally been ordered by the Brazilian Navy with the name Jaguaribe in the late 1930s, but was bought by the Royal Navy after the beginning of World War II in September 1939 and later renamed. ', 'https://en.wikipedia.org/wiki/HMS_Highlander_(H44)');
INSERT INTO sentence (title, text, url) VALUES ('Gaunt biplane no.2', 'The Gaunt biplane no.2 Baby was a single-engine, single-seat biplane, designed by John Gaunt and flown by him with some success from Southport sands in Lancashire, England during the summer of 1911.', 'https://en.wikipedia.org/wiki/Gaunt_biplane_no.2');
INSERT INTO sentence (title, text, url) VALUES ('Hongwu Emperor', 'The Hongwu Emperor (21 October 1328 – 24 June 1398), personal name Zhu Yuanzhang (Chinese: 朱元璋; Wade–Giles: Chu Yuan-chang), was the founding emperor of the Ming dynasty.', 'https://en.wikipedia.org/wiki/Hongwu_Emperor');
INSERT INTO sentence (title, text, url) VALUES ('History of Canada', 'Prior to European colonization, the lands encompassing present-day Canada were inhabited for millennia by Indigenous peoples, with distinct trade networks, spiritual beliefs, and styles of social organization.', 'https://en.wikipedia.org/wiki/History_of_Canada');
INSERT INTO sentence (title, text, url) VALUES ('Science Fiction Quarterly', 'Science Fiction Quarterly was an American pulp science fiction magazine that was published from 1940 to 1943 and again from 1951 to 1958. Charles Hornig served as editor for the first two issues; Robert A. W. Lowndes edited the remainder.', 'https://en.wikipedia.org/wiki/Science_Fiction_Quarterly');
